import { useState } from "react"

export default function Todo(){
    const [title,setTitle] = useState("");
    const [description,setDescription] = useState("");
    const [todos, setTodos] = useState([]);
    const [error, setError] = useState("");
    const [message, setMessage] = useState("");


    const apiUrl = "http://localhost:8000";

    const handleSubmit =() =>{
        setError("");
        if(title.trim()!='' && description.trim()!=''){
            fetch(apiUrl+"/todo",{
                method:"POST",
                headers:{
                    "Content-Type":"application/json"
                },
                body:JSON.stringify({title,description})
            }).then((res)=> {
                if(res.ok){
                    setTodos([...todos,{title,description}]);
                    setMessage("Item added successfully");
                    setTimeout(()=>{
                        setMessage("");
                    },2000)
                }else{
                    setError("Unable to create Todo item");
                }
            }).catch(()=>{
                setError("Unable to create Todo item");
            })
        }
    }

    return <>
    <div className="row p-3 bg-success text-light">
        <h1>Todo Project with MERN stack</h1>
    </div>
    <div className="row mt-3">
    <h3>Add Item</h3>
    {message && <p className="text-success">{message}</p>}
    <div className="form-group d-flex gap-2">
    <input placeholder="Title" onChange={(e)=> setTitle(e.target.value)} value={title} className="form-control" type="text" />
    <input placeholder="Description" onChange={(e)=> setDescription(e.target.value)} value={description} className="form-control" type="text" />
    <button className="btn btn-" onClick={handleSubmit}>Submit</button>
    </div>
    {error && <p className="text-danger">{error}</p>}
    </div>
    <div className="row mt-3">
        <h3>Lists </h3>
        <ul className="list-group">
            <li className="list-group-item d-flex justify-content-between align-items-center">
                <span>Item Text</span>
                <div className="d-flex gap-2">
                <button className="btn btn-warning">Edit</button>
                <button className="btn btn-danger">Delete</button>
                </div>
                
            </li>

        </ul>
    </div>
    </>
}
