import { useEffect, useState } from "react";

export default function Todo() {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [todos, setTodos] = useState([]);
    const [error, setError] = useState("");
    const [message, setMessage] = useState("");
    const [editId, setEditId] = useState(-1);
    const [editTitle, setEditTitle] = useState("");
    const [editDescription, setEditDescription] = useState("");

    const apiUrl = "http://localhost:8000";

    useEffect(() => {
        getItems();
    }, []);

    const getItems = () => {
        fetch(`${apiUrl}/todo`)
            .then((res) => res.json())
            .then((data) => setTodos(data))
            .catch(() => setError("Failed to fetch Todo items"));
    };

    const handleSubmit = () => {
        setError("");
        if (title.trim() !== "" && description.trim() !== "") {
            fetch(`${apiUrl}/todo`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ title, description }),
            })
                .then((res) => res.json())
                .then((data) => {
                    setTodos([...todos, data]); // Append the newly created item
                    setTitle("");
                    setDescription("");
                    setMessage("Item added successfully");
                    setTimeout(() => setMessage(""), 2000);
                })
                .catch(() => setError("Unable to create Todo item"));
        }
    };

    const handleEdit = (item) => {
        setEditId(item._id);
        setEditTitle(item.title);
        setEditDescription(item.description);
    };

    const handleUpdate = () => {
        setError("");
        if (editTitle.trim() !== "" && editDescription.trim() !== "") {
            fetch(`${apiUrl}/todo/${editId}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ title: editTitle, description: editDescription }),
            })
                .then((res) => {
                    if (res.ok) {
                        setTodos(
                            todos.map((item) =>
                                item._id === editId ? { ...item, title: editTitle, description: editDescription } : item
                            )
                        );
                        setMessage("Item updated successfully");
                        setTimeout(() => setMessage(""), 3000);
                        setEditId(-1);
                    } else {
                        setError("Unable to update Todo item");
                    }
                })
                .catch(() => setError("Unable to update Todo item"));
        }
    };

    const handleEditCancel = () => {
        setEditId(-1);
    };

    const handleDelete = (id) => {
        if (window.confirm("Are you sure you want to delete this item?")) {
            fetch(`${apiUrl}/todo/${id}`, { method: "DELETE" })
                .then(() => setTodos(todos.filter((item) => item._id !== id)))
                .catch(() => setError("Failed to delete item"));
        }
    };

    return (
        <div className="container">
            <div className="row p-3 bg-success text-light">
                <h1>Todo Project with MERN Stack</h1>
            </div>

            <div className="row mt-3">
                <h3>Add Item</h3>
                {message && <p className="text-success">{message}</p>}
                {error && <p className="text-danger">{error}</p>}

                <div className="form-group d-flex gap-2">
                    <input
                        placeholder="Title"
                        onChange={(e) => setTitle(e.target.value)}
                        value={title}
                        className="form-control"
                        type="text"
                    />
                    <input
                        placeholder="Description"
                        onChange={(e) => setDescription(e.target.value)}
                        value={description}
                        className="form-control"
                        type="text"
                    />
                    <button className="btn btn-dark" onClick={handleSubmit}>
                        Submit
                    </button>
                </div>
            </div>

            <div className="row mt-3">
                <h3>List</h3>
                <ul className="list-group gap-3">
                    {todos.map((item) => (
                        <li key={item._id} className="list-group-item bg-info d-flex justify-content-between align-items-center">
                            <div className="d-flex flex-column">
                                {editId === item._id ? (
                                    <div className="form-group d-flex gap-2">
                                        <input
                                            placeholder="Title"
                                            onChange={(e) => setEditTitle(e.target.value)}
                                            value={editTitle}
                                            className="form-control"
                                            type="text"
                                        />
                                        <input
                                            placeholder="Description"
                                            onChange={(e) => setEditDescription(e.target.value)}
                                            value={editDescription}
                                            className="form-control"
                                            type="text"
                                        />
                                    </div>
                                ) : (
                                    <>
                                        <span className="fw-bold">{item.title}</span>
                                        <span>{item.description}</span>
                                    </>
                                )}
                            </div>

                            <div className="d-flex gap-2">
                                {editId === item._id ? (
                                    <>
                                        <button className="btn btn-success" onClick={handleUpdate}>
                                            Update
                                        </button>
                                        <button className="btn btn-danger" onClick={handleEditCancel}>
                                            Cancel
                                        </button>
                                    </>
                                ) : (
                                    <>
                                        <button className="btn btn-warning" onClick={() => handleEdit(item)}>
                                            Edit
                                        </button>
                                        <button className="btn btn-danger" onClick={() => handleDelete(item._id)}>
                                            Delete
                                        </button>
                                    </>
                                )}
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}
